// @generated: @expo/next-adapter@2.1.52
import React, { useState } from "react";
import { StyleSheet, Text, TouchableOpacity, View } from "react-native";

export default function App() {
    const [withChecks, setWithChecks] = useState([]);
    const [withoutChecks, setWithoutChecks] = useState([]);
    const [withChecksRunning, setWithChecksRunning] = useState(false);

    const addLog = (text, without) => {
        if (without)
            setWithChecks([
                { time: Date().toString(), text: "already running" },
                ...withChecks,
            ]);
        else
            setWithChecks([
                { time: Date().toString(), text: "already running" },
                ...withChecks,
            ]);
    };

    const startPoll = async () => {
        try {
            if (withChecksRunning) {
                setWithChecks([
                    { time: Date().toString(), text: "already running" },
                    ...withChecks,
                ]);
                return;
            }

            if (source) source.close();
            if (timerClose) clearTimeout(timerClose);
            poll.resources = pollResources;
            // source = new SSE(getBaseURL() + '/document/poll', {
            source = new SSE(getBaseURL() + "/document/poll", {
                headers: { "Content-Type": "application/json" },
                payload: JSON.stringify({ pollResources }),
            });
            timerClose = setTimeout(() => {
                delete poll.resources;
                startPoll(pollResources, _reqNum);
            }, 20000);
            source.addEventListener("message", (e) => {
                setData({ serverDown: false });
                const news = JSON.parse(e.data);
                console.log("message", news);
                switch (news.messageCode) {
                    case "updateResource":
                        props.updateResource(
                            news.code,
                            news.id,
                            news.update,
                            () => fetchData([news.id], news.code)
                        );
                        break;
                    case "addResource":
                        props.saveResource(news.resources, news.type);
                        break;
                    case "notFoundResource":
                        props.addNotFoundResources(news._id);
                        break;
                    case "ping":
                        if (timerClose) clearTimeout(timerClose);
                        timerClose = setTimeout(() => {
                            delete poll.resources;
                            startPoll(pollResources, _reqNum);
                        }, 20000);

                        break;
                    case "updateLocale":
                        if (news.value && news.value.length === 3) {
                            const [name, version, locale] = news.value;
                            AsyncStorage.setItem(`${name}_v`, version);
                            AsyncStorage.setItem(
                                `${name}_l`,
                                JSON.stringify(locale)
                            );

                            setData({
                                languageUpdated: true,
                            });
                        }
                        break;
                    case "noLocale":
                        if (news.value) {
                            if (props.store.language === news.value) {
                                setData({
                                    language: "en",
                                });
                            }
                            lastTimeConstantsRefreshed = 0;
                        }
                        break;
                    case "refreshConstants":
                        if (news.value) setData(news.value);

                        lastTimeConstantsRefreshed = dayjs().valueOf();

                        break;
                }
            });

            source.addEventListener("error", function () {
                setTimeout(() => {
                    checkResourcesToPoll(_reqNum);
                }, 10000);
            });

            source.stream();
        } catch (error) {
            setTimeout(() => {
                checkResourcesToPoll(_reqNum);
            }, 10000);
        }
    };
    return (
        <View style={styles.container}>
            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "green",
                    color: "white",
                }}
            >
                Start poll (checks every 20 sec)
            </TouchableOpacity>
            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "red",
                    color: "white",
                }}
            >
                Stop poll
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => setWithChecks([])}
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "grey",
                    color: "white",
                }}
            >
                Clear log
            </TouchableOpacity>
            <Text>Log</Text>
            {withChecks.map((item) => (
                <View style={{ flexDirection: "row", flex: 1 }}>
                    <View>
                        <Text>{item.time}</Text>
                    </View>
                    <View>
                        <Text>{item.text}</Text>
                    </View>
                </View>
            ))}

            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "green",
                    color: "white",
                }}
            >
                Start without checks
            </TouchableOpacity>
            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "red",
                    color: "white",
                }}
            >
                Stop
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => setWithChecks([])}
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "grey",
                    color: "white",
                }}
            >
                Clear log
            </TouchableOpacity>
            <Text>Log</Text>
            {withChecks.map((item) => (
                <View style={{ flexDirection: "row", flex: 1 }}>
                    <View>
                        <Text>{item.time}</Text>
                    </View>
                    <View>
                        <Text>{item.text}</Text>
                    </View>
                </View>
            ))}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    text: {
        fontSize: 16,
    },
});
