// @generated: @expo/next-adapter@2.1.52
import React, { useState } from "react";
import { StyleSheet, Text, TouchableOpacity, View } from "react-native";

let sourceWith;
let sourceWithout;

let timerWith;

export default function App() {
    const [withChecks, setWithChecks] = useState([]);
    const [withoutChecks, setWithoutChecks] = useState([]);
    const [withChecksRunning, setWithChecksRunning] = useState(false);

    const addLog = (text, without) => {
        if (without)
            setWithoutChecks([
                { time: Date().toString(), text },
                ...withoutChecks,
            ]);
        else setWithChecks([{ time: Date().toString(), text }, ...withChecks]);
    };

    const startPoll = async () => {
        try {
            if (withChecksRunning) {
                addLog("already running");
                return;
            }
            addLog("starting");
            setWithChecksRunning(true);

            if (sourceWith) sourceWith.close();
            if (timerWith) clearTimeout(timerWith);

            source = new SSE(getBaseURL() + "/document/poll", {
                headers: { "Content-Type": "application/json" },
                payload: JSON.stringify({ pollResources }),
            });

            timerClose = setTimeout(() => {
                addLog("closed because no ping");

                setWithChecksRunning(false);
            }, 20000);
            source.addEventListener("message", (e) => {
                setData({ serverDown: false });
                const news = JSON.parse(e.data);

                switch (news.messageCode) {
                    case "time":
                        addLog(`time on server is ${news.time}`);
                        break;

                    case "ping":
                        if (timerClose) clearTimeout(timerClose);
                        timerClose = setTimeout(() => {
                            addLog("closed because no ping");

                            setWithChecksRunning(false);
                        }, 20000);

                        break;
                }
            });

            source.addEventListener("error", function () {
                addLog("closed");
                setWithChecksRunning(false);
            });
            source.stream();
        } catch (error) {
            addLog("closed");
            setWithChecksRunning(false);
        }
    };
    return (
        <View style={styles.container}>
            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "green",
                    color: "white",
                }}
            >
                <Text>Start poll (checks every 20 sec)</Text>
            </TouchableOpacity>
            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "red",
                    color: "white",
                }}
            >
                <Text>Stop poll</Text>
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => setWithChecks([])}
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "grey",
                    color: "white",
                }}
            >
                Clear log
            </TouchableOpacity>
            <Text>Log</Text>
            {withChecks.map((item) => (
                <View style={{ flexDirection: "row", flex: 1 }}>
                    <View>
                        <Text>{item.time}</Text>
                    </View>
                    <View>
                        <Text>{item.text}</Text>
                    </View>
                </View>
            ))}

            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "green",
                    color: "white",
                }}
            >
                Start without checks
            </TouchableOpacity>
            <TouchableOpacity
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "red",
                    color: "white",
                }}
            >
                Stop
            </TouchableOpacity>
            <TouchableOpacity
                onPress={() => setWithChecks([])}
                style={{
                    margin: 10,
                    padding: 10,
                    backgroundColor: "grey",
                    color: "white",
                }}
            >
                Clear log
            </TouchableOpacity>
            <Text>Log</Text>
            {withChecks.map((item) => (
                <View style={{ flexDirection: "row", flex: 1 }}>
                    <View>
                        <Text>{item.time}</Text>
                    </View>
                    <View>
                        <Text>{item.text}</Text>
                    </View>
                </View>
            ))}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    text: {
        fontSize: 16,
    },
});
